import {useState, useEffect} from 'react'
import { styled } from 'styled-components';
import {Splide, SpideSlide, SplideSlide} from '@splidejs/react-splide'
import '@splidejs/splide/dist/css/splide.min.css';
import  '@splidejs/react-splide/css';


function Trending() {
  const [trendingRepos, setTrendingRepos] = useState([]);

useEffect(() => {
  getTrending()
  }, []);

  const getTrending = async () => {

    const api = await fetch(
      'https://api.github.com/search/repositories?q=created:>2023-07-01&sort=stars&order=desc&per_page=9',
      {
        headers: {
          Authorization: process.env.GITHUB_ACCESS_TOKEN, 
        },
      }
    );
    const data = await api.json();

    const openSourceProjects = data.items.filter((repo) => {
      const allowedLicenses = ['MIT', 'Apache License 2.0', 'GNU General Public License v2.0', 'GNU General Public License v3.0', 'GNU Lesser General Public License v2.1', 'GNU Lesser General Public License v3.0'];
      return allowedLicenses.includes(repo.license?.spdx_id);
    });

    setTrendingRepos(openSourceProjects);
    console.log(data)
}


  return (
    <div>
          <Wrapper>
            <h3>Trending Repositories</h3>

            <Splide options={{
              perPage:5,
              arrows: false,
              drag: 'free',
              gap: '5rem'
            }}>
            {trendingRepos.map((repo) => {
              return(
                <SplideSlide key={repo.id}>
                  <Card>
                    <p>{repo.name}</p>
                    <img src={repo.owner.avatar_url} alt={repo.name} />
                    <Gradient />
                  </Card>
                </SplideSlide>
              )
            })}
            </Splide>
          </Wrapper>


    </div>
    )

}

const Wrapper = styled.div`
  margin: 4rem 0rem;
  `;

  const Card = styled.div`
    min-height: 25 rem;
    border-radius: 2 rem;
    overflow: hidden;
    position: relative;

    img {
      border-radius: 2rem;
      position: relative;
      left: 0;
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
    p {
      position: absolute;
      z-index: 10;
      left: 50%;
      bottom: 0%;
      transform: translate(-50%, 0%);
      color: white;
      width: 100%;
      text-align: center;
      font-weight: 600;
      font-size: 1rem;
      height: 40%;
      display: flex;
      justify-content: center;
      align-items: center;
    }`
    ;

  const Gradient = styled.div`
  z-index: 3;
  position: absolute;
  width: 100%;
  height: 100%;
  background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0,5));
  `;


export default Trending
